var Logger = module.exports = function(logType) {
    this._logType = logType;
};

Logger.prototype.log = function(logLevel, message, prefix) {
    function pad(n) {
        return n < 10 ? '0' + n.toString(10) : n.toString(10);
    }
    
    function timestamp() {
        var d = new Date();
        var date = [d.getFullYear(), pad(d.getMonth() + 1), pad(d.getDate())].join('-');
        var time = [pad(d.getHours()), pad(d.getMinutes()), pad(d.getSeconds())].join(':');
        return [date, time].join(' ');
    }
    
    var msg = timestamp() + ' [' + logLevel + '] ' + this._logType;
    if (prefix) {
        msg += ' ' + prefix;
    }
    msg += ' ::: ' + message;
    
    console.log(msg);
};